function shouldDisplayEntry(e){return autlist||-1!==e.indexOf("Chapter")&&e.length>1}if(-1===excludedLabels.indexOf(currentLabel)?autlist=!0:"Chapter"===currentLabel&&(hasChapterLabel=!0),autlist||hasChapterLabel)function autolist(e){for(var t=document.querySelector(".auto-listchap"),n=document.getElementById("chap-num"),r=document.getElementById("clOpen"),a=0;a<e.feed.entry.length;a++){for(var l=[],d=e.feed.entry[a].id.$t.substr(51,19),h=!1,u=0;u<e.feed.entry[a].category.length;u++){var i=e.feed.entry[a].category[u].term;l.push(i),-1!==excludedLabels.indexOf(i)&&(hasExcludedLabel=!0)}if(shouldDisplayEntry(l)&&d!==postID&&!encounteredEntryIDs[d]){for(var o=document.createElement("div"),s=document.createElement("a"),p=document.createElement("span"),c=0;c<e.feed.entry[a].link.length;c++)if("alternate"==e.feed.entry[a].link[c].rel){var f=e.feed.entry[a].link[c].href;-1===l.indexOf("Chapter")&&(h=!0);break}var y=e.feed.entry[a].title.$t,C=e.feed.entry[a].published.$t,m=timeAgo(C);s.href=f,s.textContent=y,p.textContent=" "+m,o.appendChild(s),o.appendChild(p),t.appendChild(o),numChapters++,encounteredEntryIDs[d]=!0,h&&(hasNonChapterLink=!0)}}0===numChapters?(n.textContent="No Chapter Yet",r.style.display="none"):(hasNonChapterLink&&numChapters--,n.textContent=numChapters)} var clModal = document.getElementById('clOpen'); var clBtn = document.getElementById('clBtn'); var clClose = document.getElementById('clClose'); clBtn.onclick = function() { openChaplistModal(); }; clClose.onclick = function() { closeChaplistModal(); }; function openChaplistModal() { clModal.style.display = 'block'; setTimeout(function() { clModal.style.opacity = '1'; }, 10); window.onclick = function(event) { if (event.target === clModal) { closeChaplistModal(); } }; modalContent.onclick = function(event) { event.stopPropagation(); }; } function closeChaplistModal() { clModal.style.opacity = '0'; setTimeout(function() { clModal.style.display = 'none'; }, 300); }
